/* disable blue filter and re-enable scroll bars*/
#container {
    filter: none !important;

    overflow: auto !important;
    overflow-x: auto !important;
    overflow-y: auto !important;

}

/* hide side add nag */
#userMessagingOverlay {
    display: none !important;
}


.global-modal {
    overflow: auto !important;
    overflow-x: auto !important;
    overflow-y: auto !important;
}

