/* Move sign-out item up to top of menu instead of hidden at the bottom */
#nav-item-signout {position: absolute !important; 
                   top: 10px; 
                   right: 10px;}

#nav-item-signout > span {font-size:120% !important;
                          font-weight:bold !important;
                          color: #d97d0d !important;
                          text-decoration-line: underline; }

/* Fix broken, sidebar for calendar obscured by menu */
.page .calendar-wrapper.hide-sidebar .calendar-sidebar-nav {
    margin-left: 0 !important;
}

/* Move upload button up to top */

#import-data-start {
    position: absolute;
        top: 345px;
        left: 140px;
}

/* fix non-active button behavior to be less intrusive*/
.btn[disabled], .btn[disabled]:hover, 
.btn[disabled]:focus, .btn.disabled, 
.btn.disabled:hover, .btn.disabled:focus { 
     opacity:1.0 !important;
     cursor: auto;
     filter: grayscale(100%) brightness(150%);
}

/* Reverse table order so last imported files are up top */
#dropzone-table {
    display: -webkit-flex; /* Safari */
    -webkit-flex-direction: column-reverse; /* Safari 6.1+ */
    flex-direction: column-reverse;
}

