/* font-size: calc( 14px + 2 * (100vw - 320px) / 1180); */
.OrderItem,
.OrderItem-name,
.OrderItem-price {
    font-size: 11pt !important;
    font-weight: normal !important;
}


._py075 {
    padding: 0px !important;
}

.QuantityBtnGroup--inOrderItem .QuantityBtnGroup-btn {
    font-size: 8pt;
    height: 15px;
    padding: 0.0rem 1.5rem;
}

.QuantityBtnGroup-btnRight {
    margin-right: 20px !important;
}



/* === Now collapse each cart entry's two-rows into one row === */


/* Name and Price Container: Float Left */
div.OrderItem-content > div:nth-child(1) {
    float: left;
    width: 65%;
}

/* Right-align the price */
div.OrderItem-content > div:nth-child(1) > div {
    color: #419927 !important;
    margin-left: auto !important;
}

/* Size and buttons Container: Float Right */
/*
div.OrderItem-content > div:nth-child(2) {    
}
*/
/* Right-align the ID and size indicators */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) {
    margin-left: auto !important;
}

/* Pad the size indicator a little on the right and turn it red */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(2) {
    color: #ea1e1e !important;
    padding-right: 20px;

    float:right;
    min-width: 8em;
}


/* == Try to fix the variable width columns of Size/Ordered/Shipped/Price == */

/* Move the Ordered N field to the right of Shipped N */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(3) {
    color:darkgrey !important;
    font-size: 11pt !important;
    float:right;
    min-width: 4em;
}
/* Hide "Ordered" label */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(3) > .OrderItem-metaItemLabel{
    display:none;
}
/* Add a seperator between Shipped N and Ordered N */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(3)::before {
    content: "/\00a0";
}

/* Set a min width for Shipped N (This gets moved to before Ordered N) */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(4) {
    color:#3cac3c !important;
    font-size: 11pt !important;

    float:right;
    min-width: 5em;
}

div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(4) > span {
    color:darkgrey !important;
}

/* Remove the Item code indicator */
div.OrderItem-content > div:nth-child(2) > div:nth-child(1) > div > span:nth-child(1) {
    display: none;
}

/* Hide "Size" label */
.MetaItem-label {
    display: none;
}


/* Try to make the right edge price a fixed width */
.OrderItem-price {
    padding-right: 10px;
    min-width:4em;    
    margin-right:0px;
}
._t-right {
    padding-left: 0px !important;
}

/* Remove (disingenuous) "login required" interstitial */
#req_login_reg_interrupt,
#window_overlay {
    display: none !important;
}

