/* undo hiding for filter main anchor element when it's "hide" class is enabled */
/*.js-filter-cards-indicator.hide > .u-text-underline {*/
.js-filter-cards-indicator.hide {
    display: unset !important;
    /*background: rgba(0,0,0,.5) !important;
    opacity: 0.25 !important;*/
    background: transparent !important;
}

/* Then, hide "filtering results" text while filter is not enabled */
.js-filter-cards-indicator.hide > .u-text-underline { 
    /*display: none !important; */
    visibility: hidden;
    text-decoration: unset;
}
/* And add a funnel/filter icon */
.js-filter-cards-indicator.hide > .u-text-underline:after { 
    /* content: '🔍 Search' !important; */
    content:'\00a0\00a0\00a0'; /*&nbsp's to set the width*/
    visibility: visible;
    padding: 5px;
    opacity: 0.6 !important;
    background-repeat:no-repeat;
    background-position: center center;    
    background-image: url(data:image/png;base64,);
}

/* And also hide close filter button icon if filter is not enabled */
.js-filter-cards-indicator.hide > .js-filter-card-clear { 
    display: none !important;   
}

